import React, { Component } from "react";
import BigCalendar from "react-big-calendar";
import moment from "moment";

import "./App.css";
import "./css/bootstrap.min.css";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "react-big-calendar-like-google/lib/css/react-big-calendar.css";
import ExampleControlSlot from './ExampleControlSlot.js'

import 'react-big-calendar/lib/less/styles.less';
import './styles.less';
import './prism.less';
import "./bootstrap/4.3.1/bootstrap.min.css";
import "./bootstrap/4.3.1/bootstrap.min.css";


const localizer = BigCalendar.momentLocalizer(moment);




class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      trainings: [],
      events: [
        {
          start: new Date(),
          end: new Date(moment().add(1, "days")),
          title: 'some title'
        }
      ]
    };
  }

  //Fetch trainings
  componentDidMount() {
    this.loadTrainings();
  }

  loadTrainings = () => {
    fetch('https://customerrest.herokuapp.com/gettrainings')
      .then(response => response.json())
      .then(jsondata => this.setState({ trainings: jsondata }))
      .catch(err => console.error(err));
  };




  EventAgenda = (event) => {
    return (
      <span>
        <em style={{ color: 'magenta' }}>{event.title}</em>
        <p>{event.desc}</p>
      </span>
    )
  }


  render() {
    let allViews = Object.keys(BigCalendar.Views).map(k => BigCalendar.Views[k])

    const eventItem = this.state.trainings.map((obj, index) => {
      let container = {};
      container.start = new Date(moment(obj.date).format('LLL'))
      container.end = new Date(moment(obj.date).add(obj.duration, 'minutes').format('LLL'));
      container.title = obj.activity;
      container.desc = '#' + obj.customer.id + ' ' + obj.customer.firstname + ' ' + obj.customer.lastname;
      return container;
    });

    console.log(eventItem);

    const Event = ({ event }) => (
      <span>
        <strong>{event.title}</strong>
        <p>{event.desc}</p>
      </span>
    );

    const EventAgenda = ({ event }) => (
      <span>
        <em style={{ color: 'magenta' }}>{event.title}</em>
        <p>{event.desc}</p>
      </span>
    );




    return (
      <div className="App">

        <BigCalendar
          localizer={localizer}
          defaultDate={new Date()}
          defaultView="month"
          events={eventItem}
          views={allViews}
          step={15}
          timeslots={8}
          style={{ height: "90vh", width: "90vw" }}
          components={{
            event: Event,
            agenda: {
              event: EventAgenda
            }

          }}
        />
      </div>
    )
  }
}

export default App;
