import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import * as moment from 'moment';

class TrainingList extends Component {
    constructor(props) {
        super(props);
        this.state = { trainings: [], open: false, message: '' };
    }
    //Fetch customers
    componentDidMount() {
        this.loadTrainings();
    }

    loadTrainings = () => {
        fetch('https://customerrest.herokuapp.com/api/trainings')
            .then(response => response.json())
            .then(jsondata => this.setState({ trainings: jsondata.content }))
            .catch(err => console.error(err));
    };

    deleteTraining = (trainingLink) => {
        if (window.confirm("Are you sure?")) {
          fetch(trainingLink, { method: 'DELETE' })
            .then(res => this.loadTrainings())
            .then(res => this.setState({ open: true, message: 'Training deleted' }))
            .catch(err => console.error(err));
        }
      };

    render() {
        const columns = [
            {
                Header: 'Date',
                accessor: 'date',
                Cell: ({ value }) => moment(value).format('LLL')

            },
            {
                Header: 'Duration',
                accessor: 'duration'
            },
            {
                Header: 'Activity',
                accessor: 'activity'
            },
            {
                Header: '',
                accessor: 'links[0].href',
                filterable: false,
                sortable: false,
                width: 100,
                Cell: ({ value }) => <Button color="secondary" onClick={() => this.deleteTraining(value)}>Delete</Button>
              },
        ];
        return (
            <div>
              <ReactTable sortable={true} filterable={true} data={this.state.trainings} columns={columns}></ReactTable>
              <Snackbar
                anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
                }}
                open={this.state.open}
          autoHideDuration={3000}
          onClose={this.handleClose}
          message={this.state.message}
        />
            </div>
        );
    }
}

export default TrainingList;
