import React, { Component } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import Shop from './components/Shop';
import Contact from './components/Contact';
import Navigator from './components/Navigator';
import Login from './components/Login';
import { firebaseAuth } from './config';

const PrivateRoute = ({ component: Component, isAuthenticated }) => (
    <Route {...rest} render={props => (
        isAuthenticated ? (
            <Component {...props} />
        ) : (
                <Redirect to={{
                    pathname: '/login',
                    state: { from: props.location }
                }} />
            )
    )} />
)

class App extends Component {
    constructor(props) {
        super(props);
        this.state = { user: null, isAuthenticated: false };
    }

    componentDidMount() {
        firebaseAuth().onAuthStateChanged((user) => {
            if (user && user.emailVerified) {
                this.setState({ user: user, isAuthenticated: true });
            }
            else {
                this.setState({ user: null, isAuthenticated: false });
            }
        });
    }

    render() {
        return (
            <div className="App">
                <BrowserRouter>
                    <div>
                        <Navigator isAuthenticated={this.state.isAuthenticated} />
                        <Switch>
                            <Route path="/login" component={Login} />
                            <Route exact path="/" component={Home} />
                            <PrivateRoute isAuthenticated={this.state.isAuthenticated} path="/contact" component={Contact} />
                            <PrivateRoute isAuthenticated={this.state.isAuthenticated} path="/shop" component={Shop} />
                            <Route render={() => <h1>Page not found</h1>} />
                        </Switch>
                    </div>
                </BrowserRouter>
            </div>
        );
    }
}

export default App;
