import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import * as moment from 'moment';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import AddCustomer from './AddCustomer';

class CustomerList extends Component {
  constructor(props) {
    super(props);
    this.state = { customers: [], open: false, message: '' };
  }
  //Fetch customers
  componentDidMount() {
    this.loadCustomers();
  }

  loadCustomers = () => {
    fetch('https://customerrest.herokuapp.com/api/customers')
      .then(response => response.json())
      .then(jsondata => this.setState({ customers: jsondata.content }))
      .catch(err => console.error(err));
  };

  deleteCustomer = (customerLink) => {
    if (window.confirm("Are you sure?")) {
      fetch(customeLink, { method: 'DELETE' })
        .then(res => this.loadCustomers())
        .then(res => this.setState({ open: true, message: 'Customer deleted' }))
        .catch(err => console.error(err));
    }
  };


  saveCustomer = (customer) => {
    fetch('https://customerrest.herokuapp.com/api/customers',
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(customer)

      })

      .then(res => this.loadCustomers())
      .then(res => this.setState({ open: true, message: 'Customer added successfully' }))
      .catch(err => console.error(err));
  }





  render() {
    const columns = [
      {
        Header: 'First name',
        accessor: 'firstname'
      },
      {
        Header: 'Last name',
        accessor: 'lastname'
      },
      {
        Header: 'Street address',
        accessor: 'streetaddress'
      },
      {
        Header: 'Postcode',
        accessor: 'postcode'
      },
      {
        Header: 'City',
        accessor: 'city'
      },
      {
        Header: 'E-mail',
        accessor: 'email'
      },
      {
        Header: 'Phone',
        accessor: 'phone'
      },
    ];
    return (
      <div>
        <AddCustomer saveCustomer={this.saveCustomer} />
        <ReactTable sortable={true} filterable={true} data={this.state.customers} columns={columns}></ReactTable>
      </div>
    );
  }
}

export default CustomerList;
