import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

class AddTraining extends Component {
  constructor(props) {
    super(props);
    this.state = { open: false, trainings: [], model: '', brand: '', color: '', fuel: '', year: '', price: '' };
  }

  handleClickOpen = () => {
    fetch('https://customerrest.herokuapp.com/api/trainings')
      .then(response => response.json())
      .then(jsondata => this.setState({ trainings: jsondata.content }))
      .catch(err => console.error(err));






    //this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  addTraining = () => {

  }

  /*addCar = () => {
      const newCar = {
          model: this.state.model,
          brand: this.state.brand,
          color: this.state.color,
          fuel: this.state.fuel,
          year: this.state.year,
          price: this.state.price
      }
      this.props.saveCar(newCar);
      this.handleClose();
  }*/

  render() {
    /*var objItem = this.state.trainings.map((obj) => {
      objArray[index] = obj.activity;
    })

    console.log(objArray);*/

    var rockets = [
      { country:'Russia', launches:32 },
      { country:'US', launches:23 },
      { country:'China', launches:16 },
      { country:'Europe(ESA)', launches:7 },
      { country:'India', launches:4 },
      { country:'Japan', launches:3 }
  ];

  var launchOptimistic = rockets.map((elem) => {
    return {
      country: elem.country,
      launches: elem.launches+10,
    } 
  });

  console.log(launchOptimistic);



    return (
      <div>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">New Car</DialogTitle>
          <DialogContent>

            <TextField onChange={this.handleChange} autoFocus margin="dense" name="brand" label="Brand" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="model" label="Model" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="color" label="Color" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="fuel" label="Fuel" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="year" label="Year" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="price" label="Price" fullWidth />
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
                        </Button>
            <Button onClick={this.addTraining} color="primary">
              Save
                        </Button>

          </DialogActions>
        </Dialog>
        <Button color="primary" onClick={this.handleClickOpen}>Add training</Button>

      </div>
    );
  }
}

export default AddTraining;

