import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

class EditTraining extends Component {
    constructor(props) {
        super(props);
        this.state = { open: false, date: '', duration: '', activity: '', customer: '' };
      }

      handleClickOpen = () => {
        this.setState({
          open: true,
          date: this.props.training.date,
          duration: this.props.training.duration,
          acitivity: this.props.training.activity,
          //customer: this.props.training.customer
        });
      };

      handleClose = () => {
        this.setState({ open: false });
      };

      handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

      renewTraining = () => {
        const modifiedTraining = {
          date: this.state.date,
          duration: this.state.duration,
          activity: this.state.activity,
          //postcode: this.state.postcode,
        }
        this.props.modifyTraining(this.props.link, modifiedTraining);
        this.handleClose();
      }



    render() {
        return (
            <div>

            </div>
        );
    }
}

export default EditTraining;
