import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import * as moment from 'moment';

class TrainingList extends Component {
    constructor(props) {
        super(props);
        this.state = { trainings: [], open: false, message: '' };
    }
    //Fetch customers
    componentDidMount() {
        this.loadCustomers();
    }

    loadCustomers = () => {
        fetch('https://customerrest.herokuapp.com/api/trainings')
            .then(response => response.json())
            .then(jsondata => this.setState({ trainings: jsondata.content }))
            .catch(err => console.error(err));
    };

    render() {
        const columns = [
            {
                Header: 'Date',
                accessor: 'date',
                Cell: ({ value }) => moment(value).format('MMMM Do YYYY, h:mm:ss a')

            },
            {
                Header: 'Duration',
                accessor: 'duration'
            },
            {
                Header: 'Activity',
                accessor: 'activity'
            },
        ];
        return (
            <div>
                <ReactTable sortable={true} filterable={true} data={this.state.trainings} columns={columns}></ReactTable>
            </div>
        );
    }
}

export default TrainingList;
