import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import * as moment from 'moment';

class AddNewTraining extends Component {
  constructor(props) {
    super(props);
    this.state = { open: false, date: '', duration: '', activity: '' };
  }

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  setTraining = () => {
    const newTraining = {
      firstname: this.state.date,
      lastname: this.state.duration,
      streetaddress: this.state.activity,
    }

    console.log(moment('2019-04-02').format());
    //this.props.saveTraining(newTraining);
    this.handleClose();
  }


  render() {
    return (
      <div>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">New Training</DialogTitle>
          <DialogContent>
            <TextField onChange={this.handleChange} autoFocus margin="dense" name="date" label="Date" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="duration" label="Duration" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="activity" label="Activity" fullWidth />

          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
                  </Button>
            <Button onClick={this.setTraining} color="primary">
              Save
                  </Button>
          </DialogActions>
        </Dialog>
        <Button color="primary" onClick={this.handleClickOpen}>Add New Training</Button>




      </div>
    );
  }
}

export default AddNewTraining;
