import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Snackbar from '@material-ui/core/Snackbar';
import * as moment from 'moment';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import AddCustomer from './AddCustomer';
import EditCustomer from './EditCustomer';
import { styles, theme } from '../styleDef';


class CustomerList extends Component {
  constructor(props) {
    super(props);
    this.state = { customers: [], open: false, message: '' };
  }
  //Fetch customers
  componentDidMount() {
    this.loadCustomers();
  }

  loadCustomers = () => {
    fetch('https://customerrest.herokuapp.com/api/customers')
      .then(response => response.json())
      .then(jsondata => this.setState({ customers: jsondata.content }))
      .catch(err => console.error(err));
  };

  deleteCustomer = (customerLink) => {
    if (window.confirm("Are you sure?")) {
      fetch(customerLink, { method: 'DELETE' })
        .then(res => this.loadCustomers())
        .then(res => this.setState({ open: true, message: 'Customer deleted' }))
        .catch(err => console.error(err));
    }
  };

  saveCustomer = (customer) => {
    fetch('https://customerrest.herokuapp.com/api/customers',
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(customer)

      })
      .then(res => this.loadCustomers())
      .then(res => this.setState({ open: true, message: 'Customer added successfully' }))
      .catch(err => console.error(err));
  }

  modifyCustomer = (link, customer) => {
    fetch(link,
      {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(customer)

      })
      .then(res => this.loadCustomers())
      .then(res => this.setState({ open: true, message: 'Changes saved' }))
      .catch(err => console.error(err));
  }

  handleClose = () => {
    this.setState({ open: false })
  }

  render() {
    const { classes } = this.props;
    const columns = [
      {
        Header: 'First name',
        accessor: 'firstname'
      },
      {
        Header: 'Last name',
        accessor: 'lastname'
      },
      {
        Header: 'Street address',
        accessor: 'streetaddress'
      },
      {
        Header: 'Postcode',
        accessor: 'postcode'
      },
      {
        Header: 'City',
        accessor: 'city'
      },
      {
        Header: 'E-mail',
        accessor: 'email'
      },
      {
        Header: 'Phone',
        accessor: 'phone'
      },
      {
        Header: '',
        accessor: 'links[0].href',
        filterable: false,
        sortable: false,
        width: 100,
        Cell: ({ value, row }) => <EditCustomer modifyCustomer={this.modifyCustomer} link={value} customer={row} />
      },
      {
        Header: '',
        accessor: 'links[0].href',
        filterable: false,
        sortable: false,
        width: 100,
        Cell: ({ value }) => 
          <Button color="primary"
            variant="contained"
            disableRipple
            className={classNames(classes.margin, classes.bootstrapRoot, classes.bootstrapPrimary)}
            onClick={() => this.deleteCustomer(value)}>Delete
          </Button>

      },

    ];
    return (
      <div>
        <AddCustomer saveCustomer={this.saveCustomer} />
        <ReactTable sortable={true} filterable={true} data={this.state.customers} columns={columns}></ReactTable>
        <Snackbar
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left',
          }}
          open={this.state.open}
          autoHideDuration={3000}
          onClose={this.handleClose}
          message={this.state.message}
        />
      </div>
    );
  }
}

CustomerList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CustomerList);
