import React, { Component } from 'react';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import purple from '@material-ui/core/colors/purple';
import green from '@material-ui/core/colors/green';
import red from '@material-ui/core/colors/red';


export const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
  margin: {
    margin: theme.spacing.unit,
  },
  button: {
    margin: theme.spacing.unit,
  },
  leftIcon: {
    marginRight: theme.spacing.unit,
  },
  rightIcon: {
    marginLeft: theme.spacing.unit,
  },
  iconSmall: {
    fontSize: 20,
  },
  cssRoot: {
    color: theme.palette.getContrastText(purple[500]),
    backgroundColor: purple[500],
    '&:hover': {
      backgroundColor: purple[700],
    },
  },
  bootstrapRoot: {
    boxShadow: 'none',
    textTransform: 'none',
    fontSize: 16,
    padding: '6px 12px',
    border: '1px solid',
    lineHeight: 1.5,
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
  },



  /*secondary:#6c757d;    --success:#28a745;
  danger:#dc3545;

  btn-primary:hover{color:#fff;background-color:#0069d9;border-color:#0062cc}.btn-primary.focus,
  .btn-primary:focus{box-shadow:0 0 0 .2rem rgba(38,143,255,.5)}.btn-primary.disabled,
  .btn-primary:disabled{color:#fff;background-color:#007bff;border-color:#007bff}.btn-primary:not(:disabled):not(.disabled).active,
  .btn-primary:not(:disabled):not(.disabled):active,.show>.btn-primary.dropdown-toggle{color:#fff;background-color:#0062cc;border-color:#005cbf}.btn-primary:not(:disabled):not(.disabled).active:focus,
  .btn-primary:not(:disabled):not(.disabled):active:focus,.show>.btn-primary.dropdown-toggle:focus{box-shadow:0 0 0 .2rem rgba(38,143,255,.5)}.btn-secondary{color:#fff;background-color:#6c757d;border-color:#6c757d}.

 btn-danger:hover{color:#fff;background-color:#c82333;border-color:#bd2130}.btn-danger.focus,
 .btn-danger:focus{box-shadow:0 0 0 .2rem rgba(225,83,97,.5)}.btn-danger.disabled,
 .btn-danger:disabled{color:#fff;background-color:#dc3545;border-color:#dc3545}.btn-danger:not(:disabled):not(.disabled).active,
 .btn-danger:not(:disabled):not(.disabled):active,.show>.btn-danger.dropdown-toggle{color:#fff;background-color:#bd2130;border-color:#b21f2d}.btn-danger:not(:disabled):not(.disabled).active:focus,.btn-danger:not(:disabled):not(.disabled):active:focus,.show>.btn-danger.dropdown-toggle:focus{box-shadow:0 0 0 .2rem rgba(225,83,97,.5)}.btn-light{color:#212529;background-color:#f8f9fa;border-color:#f8f9fa}.
*/

  bootstrapPrimary: {
    backgroundColor: '#007bff',
    borderColor: '#fff',
    '&:hover': {
      backgroundColor: '#0069d9',
      //borderColor: '#0062cc',
    },
    '&:active': {
      boxShadow: 'none',
      backgroundColor: '#0062cc',
      //borderColor: '#005cbf',
    },
    '&:focus': {
      boxShadow: 'none',
      //boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
      //boxShadow: '0 0 0 0.2rem rgba(38,143,255,.5)',
    },
  },
  bootstrapSecondary: {
    backgroundColor: '#6c757d',
    borderColor: '#6c757d',
    '&:hover': {
      backgroundColor: '#5a6268',
      borderColor: '#545b62',
    },
    '&:active': {
      boxShadow: 'none',
      backgroundColor: '#545b62',
      borderColor: '#4e555b',
    },
    //'&:focus': {
    //boxShadow: '0 0 0 0.2rem rgba(130,138,145,.5)',
    //},
  },
  bootstrapDanger: {
    backgroundColor: '#dc3545',
    borderColor: '#dc3545',
    '&:hover': {
      backgroundColor: '#c82333',
      borderColor: '#bd2130',
    },
    '&:active': {
      boxShadow: 'none',
      backgroundColor: '#bd2130',
      borderColor: '#b21f2d',
    },
    //'&:focus': {
    //boxShadow: '0 0 0 0.2rem rgba(225,83,97,.5)',
    //},
  },
});

export const theme = createMuiTheme({
  palette: {
    primary: purple,
    secondary: green,
    error: red,
  },
  typography: {
    useNextVariants: true,
  },
});
