import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import * as moment from 'moment';
import { withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
});




class AddNewTraining extends Component {
  constructor(props) {
    super(props);
    this.state = { open: false, date: '', duration: '', activity: '', customers: [], item: ''  };
  }

  loadCustomers = () => {
    fetch('https://customerrest.herokuapp.com/api/customers')
      .then(response => response.json())
      .then(jsondata => this.setState({ customers: jsondata.content }))
      .catch(err => console.error(err));
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  setTraining = () => {
    const newTraining = {
      date: moment(this.state.date).format(),
      duration: parseInt(this.state.duration),
      activity: this.state.activity,
    }

    //console.log(moment('2019-04-02 11:15 AM').format());
    //console.log(moment('2019-04-02 11:15 PM').format());
    //console.log(moment('2019-04-02T23:15:00+03:00').format('LLL'));

    this.props.saveTraining(newTraining);
    this.handleClose();
  }


  render() {
    return (
      <div>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">New Training</DialogTitle>
          <DialogContent>
            <TextField onChange={this.handleChange} autoFocus margin="dense" name="date" label="Date" placeholder='YYYY-MM-DD HH:MM' fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="duration" label="Duration" fullWidth />
            <TextField onChange={this.handleChange} margin="dense" name="activity" label="Activity" fullWidth />


            <form className={classes.container}>
              <FormControl className={classes.formControl}>
                <InputLabel htmlFor="item-native-simple">Customer</InputLabel>
                <Select
                  native
                  value={this.state.item}
                  onChange={this.handleChange}
                  input={<Input id="item-native-simple" />}
                >
                  <option value="" />
                  {objItem}
                </Select>
              </FormControl>
            </form>

          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
                  </Button>
            <Button onClick={this.setTraining} color="primary">
              Save
                  </Button>
          </DialogActions>
        </Dialog>
        <Button color="primary" onClick={this.handleClickOpen}>Add New Training</Button>




      </div>
    );
  }
}

export default AddNewTraining;
