import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

class EditTraining extends Component {
    constructor(props) {
        super(props);
        this.state = { open: false, firstname: '', lastname: '', streetaddress: '', postcode: '' };
      }

      handleClickOpen = () => {
        this.setState({
          open: true,
          date: this.props.customer.date,
          duration: this.props.customer.duration,
          acitivity: this.props.customer.activity,
          customer: this.props.customer.phone
        });
      };

      handleClose = () => {
        this.setState({ open: false });
      };

      handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

      renewCustomer = () => {
        const modifiedCustomer = {
          firstname: this.state.firstname,
          lastname: this.state.lastname,
          streetaddress: this.state.streetaddress,
          postcode: this.state.postcode,
          city: this.state.city,
          email: this.state.email,
          phone: this.state.phone
        }
        this.props.modifyCustomer(this.props.link, modifiedCustomer);
        this.handleClose();
      }



    render() {
        return (
            <div>

            </div>
        );
    }
}

export default EditTraining;
