import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import * as moment from 'moment';
import { withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
});

class AddTraining extends Component {
  constructor(props) {
    super(props);
    this.state = { open: false, trainings: [], item: '' };
  }

  //Fetch trainings
  componentDidMount() {
    this.loadTrainings();
  }

  loadTrainings = () => {
    fetch('https://customerrest.herokuapp.com/api/trainings')
      .then(response => response.json())
      .then(jsondata => this.setState({ trainings: jsondata.content }))
      .catch(err => console.error(err));
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = (event) => {
    this.setState({ item: event.target.value });
  };

  addTraining = () => {
    //console.log(this.state.item);
    const item = parseInt(this.state.item);
    //console.log(this.state.trainings[item].activity);

    /*const newTraining = {
      date: this.state.trainings[item].date,
      duration: this.state.trainings[item].duration,
      activity: this.state.trainings[item].activity,
    }*/

    const customerRef = {
      date: this.state.trainings[item].date,
      duration: this.state.trainings[item].duration,
      activity: this.state.trainings[item].activity,
      customer: this.props.link,
    }
    console.log()

    console.log(this.props.link);
    console.log(item);


    this.props.updateTraining(itemId, customerRef);
    this.handleClose();
  }

  /*addCar = () => {
      const newCar = {
          model: this.state.model,
          brand: this.state.brand,
          color: this.state.color,
          fuel: this.state.fuel,
          year: this.state.year,
          price: this.state.price
      }
      this.props.saveCar(newCar);
      this.handleClose();
  }*/

  render() {
    /*var objItem = this.state.trainings.map((obj) => {
      return {
        item: moment(obj.date).format('LLL') + ' ' + obj.duration + ' min ' + obj.activity
      }
    });
    console.log(objItem); */

    const { classes } = this.props;

    const objItem = this.state.trainings.map((obj, index) => {
      return (
        <option value={index}>
          {moment(obj.date).format('LLL') + ' ' + obj.duration + ' min ' + obj.activity}
        </option>
      )
    });
    console.log(objItem);

    return (
      <div>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">Select training</DialogTitle>
          <DialogContent>
            <form className={classes.container}>
              <FormControl className={classes.formControl}>
                <InputLabel htmlFor="item-native-simple">Training</InputLabel>
                <Select
                  native
                  value={this.state.item}
                  onChange={this.handleChange}
                  input={<Input id="item-native-simple" />}
                >
                  <option value="" />
                  {objItem}
                </Select>
              </FormControl>
            </form>
          </DialogContent>



          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={this.addTraining} color="primary">
              Save
            </Button>

          </DialogActions>
        </Dialog>
        <Button color="primary" onClick={this.handleClickOpen}>Add training</Button>

      </div>
    );
  }
}

AddTraining.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(AddTraining);


